script:
frappe.ui.form.on('Payment Entry', {
    // Set default when form loads
    onload: function(frm) {
        if (!frm.doc.custom_post_date) {
            frm.set_value('custom_post_date', frappe.datetime.get_today());
        }
    },
    
    // Copy posting date to custom field
    posting_date: function(frm) {
        if (frm.doc.posting_date) {
            frm.set_value('custom_post_date', frm.doc.posting_date);
        }
    },
    
    // Initialize on form load
    refresh: function(frm) {
        if (frm.doc.posting_date && !frm.doc.custom_post_date) {
            frm.set_value('custom_post_date', frm.doc.posting_date);
        }
    }
});

frappe.ui.form.on('Payment Entry', {
    setup: function(frm) {
        // Set default posting date to today if empty
        if (!frm.doc.custom_post_date) {
            frm.set_value('custom_post_date', frappe.datetime.get_today());
        }
    },
    
    custom_post_date: function(frm) {
        // Use today's date if posting date is empty
        const targetDate = frm.doc.custom_post_date || frappe.datetime.get_today();
        
        frappe.call({
            method: 'frappe.client.get_value',
            args: {
                'doctype': 'Fiscal Year',
                'filters': {
                    'year_start_date': ['<=', targetDate],
                    'year_end_date': ['>=', targetDate]
                },
                'fieldname': 'year_end_date'
            },
            callback: function(r) {
                if (r.message && r.message.year_end_date) {
                    // Extract just the year portion (YYYY-MM-DD → YYYY)
                    const fiscalYearEnd = r.message.year_end_date.split('-')[0];
                    frm.set_value('custom_fiscal_year_end', fiscalYearEnd);
                } else {
                    frappe.msgprint(__("No fiscal year found for this date. Please check Fiscal Year settings."));
                }
            }
        });
    },
    
    refresh: function(frm) {
        // Initialize if values are empty
        if (!frm.doc.custom_post_date) {
            this.setup(frm);
        }
        if (!frm.doc.custom_fiscal_year_end && frm.doc.custom_post_date) {
            this.custom_post_date(frm);
        }
    }
});

